import React, { useState } from "react";
import "./Form.css";

const Form = () => {
    const [numMeds, setNumMeds] = useState(0);
    const [medicines, setMedicines] = useState([]);

    const handleNumMedsChange = (e) => {
        const count = parseInt(e.target.value, 10) || 0;
        setNumMeds(count);
        setMedicines(Array(count).fill({ name: "", dosage: "", time: "" }));
    };

    const handleMedicineChange = (index, field, value) => {
        const updatedMeds = [...medicines];
        updatedMeds[index][field] = value;
        setMedicines(updatedMeds);
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        alert("Submitted successfully!");
    };

    return (
        <div className="form-container">
            <h2>Medicine Reminder Form</h2>
            <form onSubmit={handleSubmit}>
                <label htmlFor="name">Name:</label>
                <input type="text" id="name" name="name" required placeholder="Enter your name" />

                <label htmlFor="age">Age:</label>
                <input type="number" id="age" name="age" required placeholder="Enter your age" />

                <label htmlFor="doctor">Doctor Name:</label>
                <input type="text" id="doctor" name="doctor" required placeholder="Doctor's Name" />

                <label htmlFor="numMeds">Number of Medicines:</label>
                <input
                    type="number"
                    id="numMeds"
                    name="numMeds"
                    min="1"
                    value={numMeds}
                    onChange={handleNumMedsChange}
                    required
                    placeholder="Enter number of medicines"
                />

                <div id="medsContainer" className="meds-container">
                    {medicines.map((med, index) => (
                        <div key={index} className="medicine-section">
                            <label>Medicine {index + 1} Name:</label>
                            <input
                                type="text"
                                name={`medicine${index + 1}`}
                                value={med.name}
                                onChange={(e) => handleMedicineChange(index, "name", e.target.value)}
                                required
                                placeholder="Enter medicine name"
                            />

                            <label>Dosage:</label>
                            <input
                                type="text"
                                name={`dosage${index + 1}`}
                                value={med.dosage}
                                onChange={(e) => handleMedicineChange(index, "dosage", e.target.value)}
                                required
                                placeholder="Enter dosage (e.g. 500mg)"
                            />

                            <label>Reminder Time:</label>
                            <input
                                type="time"
                                name={`time${index + 1}`}
                                value={med.time}
                                onChange={(e) => handleMedicineChange(index, "time", e.target.value)}
                                required
                            />
                        </div>
                    ))}
                </div>

                <button type="submit" className="btn">Submit</button>
            </form>
        </div>
    );
};

export default Form;
