import axios from "axios";
import React, { useState } from "react";

const CameraScanner = () => {
    const [medicineInfo, setMedicineInfo] = useState(null);
    const [imageFile, setImageFile] = useState(null);

    // Handle image selection
    const handleFileChange = (event) => {
        setImageFile(event.target.files[0]);
    };

    // Upload image to S3 & process it
    const handleUpload = async () => {
        if (!imageFile) return alert("Please select an image first!");

        const bucketName = "medbuddy-medicine-images"; // Your S3 bucket
        const imageName = `uploads/${imageFile.name}`;

        // Step 1: Upload image to S3
        const uploadUrl = `https://xqw2x3kybi.execute-api.ap-south-1.amazonaws.com/test/camerafunction`;
        await axios.put(uploadUrl, imageFile, { headers: { "Content-Type": imageFile.type } });

        // Step 2: Call Lambda function to extract medicine details
        const lambdaUrl = "https://xqw2x3kybi.execute-api.ap-south-1.amazonaws.com/test/extract";
        const response = await axios.post(lambdaUrl, { bucketName, imageName });

        // Step 3: Show results
        setMedicineInfo(response.data);
    };

    return (
        <div>
            <h2>Scan Your Medicine</h2>
            <input type="file" accept="image/*" onChange={handleFileChange} />
            <button onClick={handleUpload}>Upload & Scan</button>

            {medicineInfo && (
                <div>
                    <h3>Detected Medicine: {medicineInfo.medicineName}</h3>
                    <p>{JSON.stringify(medicineInfo.medicineDetails, null, 2)}</p>
                </div>
            )}
        </div>
    );
};

export default CameraScanner;
