import React, { useState } from "react";
import "./Form.css"; // Import the CSS file

const Form = () => {
    const [numMeds, setNumMeds] = useState(0);
    const [medicines, setMedicines] = useState([]);

    const handleNumMedsChange = (e) => {
        const count = parseInt(e.target.value, 10);
        setNumMeds(count > 0 ? count : 0);

        // Reset medicines list when number changes
        setMedicines(Array(count).fill({ name: "", dosage: "", times: ["", "", ""] }));
    };

    const handleMedicineChange = (index, field, value) => {
        const updatedMeds = [...medicines];
        if (field === "times") {
            updatedMeds[index].times[value.index] = value.time;
        } else {
            updatedMeds[index][field] = value;
        }
        setMedicines(updatedMeds);
    };

    const removeMedicine = (index) => {
        setMedicines(medicines.filter((_, i) => i !== index));
        setNumMeds(numMeds - 1);
    };

    return (
        <div className="form-container">
            <h2>Medicine Reminder Form</h2>
            <form>
                <label>Name:</label>
                <input type="text" placeholder="Enter your name" required />

                <label>Age:</label>
                <input type="number" placeholder="Enter your age" required />

                <label>Doctor Name:</label>
                <input type="text" placeholder="Doctor's Name" required />

                <label>Number of Medications:</label>
                <input
                    type="number"
                    min="1"
                    value={numMeds}
                    onChange={handleNumMedsChange}
                    placeholder="Enter number of medicines"
                    required
                />

                <div id="medicine-list">
                    {medicines.map((med, index) => (
                        <div className="medicine-section" key={index}>
                            <label>Medicine Name:</label>
                            <input
                                type="text"
                                placeholder="Enter medicine name"
                                value={med.name}
                                onChange={(e) => handleMedicineChange(index, "name", e.target.value)}
                                required
                            />

                            <label>Dosage:</label>
                            <input
                                type="text"
                                placeholder="Enter dosage (e.g. 500mg)"
                                value={med.dosage}
                                onChange={(e) => handleMedicineChange(index, "dosage", e.target.value)}
                                required
                            />

                            <label>Reminder Times:</label>
                            {med.times.map((time, i) => (
                                <input
                                    key={i}
                                    type="time"
                                    value={time}
                                    onChange={(e) =>
                                        handleMedicineChange(index, "times", { index: i, time: e.target.value })
                                    }
                                    required
                                />
                            ))}

                            <button type="button" className="remove-btn" onClick={() => removeMedicine(index)}>
                                Remove Medicine
                            </button>
                        </div>
                    ))}
                </div>

                <button type="submit">Submit</button>
            </form>
        </div>
    );
};

export default Form;
