import axios from "axios";
import React, { useState, useRef, useEffect } from "react";

const CameraScanner = () => {
    const [medicineInfo, setMedicineInfo] = useState(null);
    const [imageFile, setImageFile] = useState(null);
    const videoRef = useRef(null);
    const canvasRef = useRef(null);

    // Automatically start camera when component mounts
    useEffect(() => {
        const startCamera = async () => {
            try {
                const stream = await navigator.mediaDevices.getUserMedia({ video: true });
                if (videoRef.current) {
                    videoRef.current.srcObject = stream;
                }
            } catch (error) {
                console.error("Error accessing camera:", error);
            }
        };

        startCamera();
    }, []);

    // Capture image from video stream
    const captureImage = () => {
        const canvas = canvasRef.current;
        const video = videoRef.current;
        if (canvas && video) {
            const context = canvas.getContext("2d");
            context.drawImage(video, 0, 0, canvas.width, canvas.height);
            canvas.toBlob((blob) => {
                const file = new File([blob], "captured-image.jpg", { type: "image/jpeg" });
                setImageFile(file);
            }, "image/jpeg");
        }
    };

    // Upload image to S3 & process it
    const handleUpload = async () => {
        if (!imageFile) return alert("Please capture an image first!");

        const bucketName = "medbuddy-medicine-images"; // Your S3 bucket
        const imageName = `uploads/${imageFile.name}`;

        // Step 1: Upload image to S3
        const uploadUrl = `https://xqw2x3kybi.execute-api.ap-south-1.amazonaws.com/test/camerafunction`;
        await axios.put(uploadUrl, imageFile, { headers: { "Content-Type": imageFile.type } });

        // Step 2: Call Lambda function to extract medicine details
        const lambdaUrl = "https://xqw2x3kybi.execute-api.ap-south-1.amazonaws.com/test/extract";
        const response = await axios.post(lambdaUrl, { bucketName, imageName });

        // Step 3: Show results
        setMedicineInfo(response.data);
    };

    return (
        <div>
            <h2>Scan Your Medicine</h2>
            <video ref={videoRef} autoPlay playsInline width="300" height="200"></video>
            <canvas ref={canvasRef} width="300" height="200" style={{ display: "none" }}></canvas>
            <button onClick={captureImage}>Capture Image</button>
            <button onClick={handleUpload}>Upload & Scan</button>

            {medicineInfo && (
                <div>
                    <h3>Detected Medicine: {medicineInfo.medicineName}</h3>
                    <p>{JSON.stringify(medicineInfo.medicineDetails, null, 2)}</p>
                </div>
            )}
        </div>
    );
};

export default CameraScanner;
